import React, { useEffect, useState } from 'react'
import axios from 'axios';
import Movie_DATA from './Movie.data'
import Preview from '../movie-preview/Preview';
import { Row, Col, Container } from 'react-bootstrap';

class Movies extends React.Component {
  constructor(props) {
    super(props)

    this.handleShowMore = this.handleShowMore.bind(this)
    this.state = {
      collections: Movie_DATA,
      showItems: 2
    }

  }
  handleShowMore() {
    this.setState({
      showItems: 
        this.state.showItems >= this.state.collections.length ?
          this.state.showItems : this.state.showItems + 1
    })
  }


  render() {

    //const { collections } = this.state;
    const collections = this.state.collections.slice(0, this.state.showItems).map(
      (item) => <div>{item}</div>
    )


    return (

      <Container>
        <Row>
          {
    collections
          }

        </Row>
      </Container>

    )
  }

}

export default Movies;
