import React, { useState, useEffect } from 'react'
import CollectionMovie from '../collection-movie/collection-movie'
import { Container, Row, Col, Button } from 'react-bootstrap';

const Preview = ({ title, items }) => {

    const [page, setPage] = useState([])
    const [showItems, setShowItems] = useState([])

    useEffect(() => {
        function handelShowMore() {
           
            setShowItems(page-1*20)
        }
    })


    //https://stackoverflow.com/questions/39455224/is-it-possible-to-map-only-a-portion-of-an-array-array-map

    return (
        <div>

            {

                item.slice(0, showItems).map(({ id, ...otherCollectionProps }) => (

                    <Container>
                        <Row>

                            <Col xs={12}>
                                <CollectionMovie key={id} {...otherCollectionProps} /> {item}</Col>

                        </Row>

                    </Container>


                )
                )

            }




        </div>
    )
}

export default Preview;
